/**
 * from_generator.js
 *
 * patters generated by the backend
 *
 * LEDstrip plugin
 */

function Generator (ledstrip) {
    
	this.ledstrip = ledstrip;
	this.ledstrip.clear();
	this.direction = 1;
	// tick counter
	this.t = 0;

	return this;
}

Generator.prototype.init = function() {}

Generator.prototype.setLEDs = function (data, size) {
    var i
    
    for (i = 0; i < size; i++) {
        this.ledstrip.buffer[i] = data[i]
    }
}

Generator.prototype.generator = function (tick) { }

Generator.prototype.animate = function() {

    // preserve our context
	animation = requestAnimationFrame(this.animate.bind(this)); 

    // calculate waves and increment tick
	this.generator(this.t++);
    
    // update strip
	this.ledstrip.send();   
}

